script.script:
if(!model.specificationCode){
var NotificationsNotOpRuleArr = cm_spoc.initInnerDS("base_NotificationsNotOpRuleArr");
model.NotificationsNotOpRuleArrDS = NotificationsNotOpRuleArr;

return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var NotificationsNotOpRuleArrRel = model.NotificationsNotOpRuleArr;
    s.specificationCode = NotificationsNotOpRuleArrRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.NotificationsNotOpRuleArrDS = a;
        NotificationsNotOpRuleArrRel = a.List;
    }
    var NotificationsNotOpRuleDS = model.NotificationsNotOpRuleArrDS.ListDS;
    for(var i=0;i<NotificationsNotOpRuleArrRel.length;i++){
        s.specificationCode = NotificationsNotOpRuleArrRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            NotificationsNotOpRuleDS[i]=r;
            var SubOperationRel = r.SubOperation;
            s.specificationCode = SubOperationRel;
            var c =  epc.getSpecificationByKey(s);
            if(!api_common.isFault(c)){
                NotificationsNotOpRuleDS[i].SubOperationDS = c;
            }
            var ENNRel = r.ExternalNotifNodeArr;
            s.specificationCode = ENNRel;
            var c =  epc.getSpecificationByKey(s);
            if(!api_common.isFault(c)){
                NotificationsNotOpRuleDS[i].ExternalNotifNodeArrDS = c;
            }
        }
    }
    return model;
}
