script.script:
if(!model.specificationCode){
var OpTaxProvinceArr = cm_spoc.initInnerDS("base_OpTaxProvinceArr");
model.OpTaxProvinceArrDS = OpTaxProvinceArr;
return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var OpTaxProvinceArrRel = model.OpTaxProvinceArr;
    s.specificationCode = OpTaxProvinceArrRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.OpTaxProvinceArrDS = a;
        OpTaxProvinceArrRel = a.List;
    }
    var OpTaxProvinceArrDS = model.OpTaxProvinceArrDS.ListDS;
    for(var i=0;i<OpTaxProvinceArrRel.length;i++){
        s.specificationCode = OpTaxProvinceArrRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            OpTaxProvinceArrDS[i]=r;
        }
    }
    return model;
}
