script.script:
/**
 * This script initialize the model for the Price Guidings array and its interfaces
 */

var baseItemCode = ui.model.baseSpecificationCode ? ui.model.baseSpecificationCode : ui.model.catalogEntity;
var nameArray = cm_spoc.getBasePriceGuidingArrayName(baseItemCode);
var nameArrayDS = nameArray+"DS";
var basePriceEvent = cm_spoc.getBasePriceEvent(baseItemCode);


//Model initialization
ui.model.BasePriceEvent = basePriceEvent;
cm_spoc.initPriceGuiding(ui.model, baseItemCode, nameArray, nameArrayDS);



//Container Initialization
var PriceGuidingContainerUI = new cm_spoc.forms.PriceGuidingContainerUI(ui.model,ui); //(model,parent)




PriceGuidingContainerUI.BaseEvent = basePriceEvent;


ui.PriceGuidingContainerUI = PriceGuidingContainerUI;


//Price Guiding Iteratos initialization
cm_spoc.initPriceGuidingUI(ui,nameArrayDS);
