script.script:
if(!model.specificationCode){
    var RGCodeArr = cm_spoc.initInnerDS("base_RGCodeArr");
    model.RGCodeArrDS = RGCodeArr;

    return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    s.specificationCode = model.RGCodeArr;
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
       model.RGCodeArrDS=r;
    }

    var RGCodeRel =  model.RGCodeArrDS.List;
    var RGCodeDS =  model.RGCodeArrDS.ListDS;
    for(var i=0;i<RGCodeRel.length;i++){
        s.specificationCode = RGCodeRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            RGCodeDS[i]=r;
        }
    }
    return model;
}
