script.script:
var finderUi =  new cm_spoc.RTFinder.UserInterface(null,ui);

if ( !ui.model.specificationCode ) {

    var emptyRes = new DataObjectList();
    finderUi.model.setList(emptyRes);

} else {
    var parentModel = "RatingTree";
    var nameArrayDS = "RTRatingTreeBranchArrDS";
    var nameArray = "RTRatingTreeBranchArr";

    var code = ui.model[nameArray];

    var result= cm_spoc.fromArrayToDOL(ui.model[nameArrayDS].ListDS);

    mapToOutputDoc(result);
    finderUi.model.updateUI();

    finderUi.model.setList(result);
}


return finderUi;



function mapToOutputDoc(result) {

    for (var t=0;t<result.length;t++ ) {
        var doc = new cm_spoc.RatingTreeDoc();
        var s = new epc.data.Specification();
        s.project =  cwa_epc.getDefaultProj();
        s.specificationCode = result[t].specificationCode;

        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
             result[t]=result[t].mapTo(doc,"cm_spoc.RatingTreeCM")[0];
        }


    }
}
