script.script:
if(ui.model.specificationCode){
 for(var i=0; i<ui.model.RPRRateArrDS.List.length; i++){

        var found = false;

       for(var j=0;j<ui.PickList.availableItems.length;j++){
        var item = ui.PickList.availableItems[j];
            if(item.id == ui.model.RPRRateArrDS.List[i]){
                found = true;
                break;
            }
        }

        if(found){
            ui.PickList.selectedItems.addLast(item);
            ui.PickList.availableItems.remove(item);
        }
    }

}
