if(!model.specificationCode){


    var RLTPDestinationTypeArr = cm_spoc.initInnerDS("base_RLTPDestinationTypeArr");
    model.RLTPDestinationTypeArrDS = RLTPDestinationTypeArr;

    var CadenceModelId = cm_spoc.initInnerDS("base_TechnicalCadenceSpec");
    model.CadenceModelIdDS = CadenceModelId;

   // var DataCadence = cm_spoc.initInnerDS("base_TechnicalCadenceSpec");
