}
else{
    var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();

   s.specificationCode = model.RLTPDestinationTypeArr;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.RLTPDestinationTypeArrDS=r;
   }

   var finalDestinationsRel = model.RLTPDestinationTypeArrDS.List;
   var finalDestinationsDS= model.RLTPDestinationTypeArrDS.ListDS;
   for(var i=0;i<finalDestinationsRel.length;i++){
       s.specificationCode = finalDestinationsRel[i];
       var r = epc.getSpecificationByKey(s);
       if(!api_common.isFault(r)){
           finalDestinationsDS[i]=r;
       }
   }

   s.specificationCode = model.CadenceModelId;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.CadenceModelIdDS=r;
   }
}


    return model;
