script.script:
if(!model.specificationCode){

   var CABOCycleChngeSpecification =  cm_spoc.initInnerDS("base_CABOCycleChngeSpecification");
   model.CABOCycleChngeSpecificationDS = CABOCycleChngeSpecification;

 }else{

   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();


   s.specificationCode = model.CABOCycleChngeSpecification;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.CABOCycleChngeSpecificationDS=r;
   }
}
return model;
