script.script:
if(!model.specificationCode){
    var RecurrentChargePhaseArr = cm_spoc.initInnerDS("base_RecurrentChargePhaseArr");
    model.RecurrentChargePhaseArrDS = RecurrentChargePhaseArr;

    return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var RecurrentChargePhaseRel = model.RecurrentChargePhaseArr;
    s.specificationCode = RecurrentChargePhaseRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.RecurrentChargePhaseArrDS = a;
        RecurrentChargePhaseRel = a.List;
    }
    var RecurrentChargePhaseDS = model.RecurrentChargePhaseArrDS.ListDS;
    for(var i=0;i<RecurrentChargePhaseRel.length;i++){
        s.specificationCode = RecurrentChargePhaseRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            RecurrentChargePhaseDS[i]=r;
        }
    }
    return model;
}
