script.script:
if(!model.specificationCode){

   var CACHCycleChngeSpecification =  cm_spoc.initInnerDS("base_CACHCycleChngeSpecification");
   model.CACHCycleChngeSpecificationDS = CACHCycleChngeSpecification;

}else{

   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();


   s.specificationCode = model.CACHCycleChngeSpecification;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.CACHCycleChngeSpecificationDS=r;
   }
}
return model;
