script.script:
if(!model.specificationCode){

   var CATUCycleChngeSpecification =  cm_spoc.initInnerDS("base_CATUCycleChngeSpecification");
   model.CATUCycleChngeSpecificationDS = CATUCycleChngeSpecification;

}else{

   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();


   s.specificationCode = model.CATUCycleChngeSpecification;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.CATUCycleChngeSpecificationDS=r;
   }
}
return model;
