script.script:
var s = new epc.data.Specification();
s.project =  cwa_epc.getDefaultProj();

if(!model.getValueByName(subOp)){
    var SubOperation = cm_spoc.initInnerDS("base_"+subOp);
    model.putValueByName(subOp+"DS",SubOperation);
}
else
{

    s.specificationCode = model.getValueByName(subOp);
    var c =  epc.getSpecificationByKey(s);
    if(!api_common.isFault(c)){
        model.putValueByName(subOp+"DS",c);
    }
}
