script.script:
if (!model.specificationCode){
    model = cm_spoc.initModelForCreation(model);
}else{
    model = cm_spoc.initModelForModification(model);

    model.TariffPlanSpecificationEnum = model.TariffPlanSpecification;

    var listRel = model.TPTypeOfServiceArrDS.ListDS;
    for ( var i = 0; i<listRel.length; i++){
        listRel[i].ServiceTariffPlanEnum = listRel[i].ServiceTariffPlanAllowedPer;
    }
}
return model;
