script.script:
if(!model.specificationCode){

    var tuCriterionArr = cm_spoc.initInnerDS("base_TUCriterionTUOriginArr");
    model.TUCriterionTUOriginArrDS = tuCriterionArr;

}
else{
   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();


   s.specificationCode =  model.TUCriterionTUOriginArr;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
       model.TUCriterionTUOriginArrDS=r;
   }


   var tuCriterionRel = model.TUCriterionTUOriginArrDS.List;
   var tuCriterionDS = model.TUCriterionTUOriginArrDS.ListDS;
   for(var i=0;i<tuCriterionRel.length;i++){
       s.specificationCode = tuCriterionRel[i];
       var r = epc.getSpecificationByKey(s);
       if(!api_common.isFault(r)){
          tuCriterionDS[i]=r;
       }
   }

}

return model;
