script.script:
if(!model.specificationCode){

    var tribeSpec = cm_spoc.initInnerDS("base_TribeSpecification");
    /*model= tribeSpec;*/
    var codeAux =  tribeSpec.specificationCode;
    model.RegExCode = codeAux.substring(codeAux.length-4, codeAux.length);
    model.RegExDescription = tribeSpec.specificationCode;
    model.InfiniteDate = false;
    model.VoiceTribe = false;
    model.SMSTribe = false;
    model.ImpSubsAllFromExToOwn = true;
    model.ExpSubsAllFromOwnToExt = true;
    model.StartDate ="1999-01-01T00:00:46Z";
    model.EndDate ="1999-01-01T00:00:46Z";

}else{
  var s = new epc.data.Specification();
  s.project =  cwa_epc.getDefaultProj();

  s.specificationCode = model;
  var r = epc.getSpecificationByKey(s);
  if(!api_common.isFault(r)){
       model= r;
  }

}
return model;
