script.script:
if (!model.specificationCode){
    model = cm_spoc.initModelForCreation(model);
}else{
    model = cm_spoc.initModelForModification(model);

    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    s.specificationCode = model.UCBOASUCBoltOnTypeAssArr;
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
        model.UCBOASUCBoltOnTypeAssArrDS=r;
    }


    var ucbota =  model.UCBOASUCBoltOnTypeAssArrDS.List;
    var ucbotaDS= model.UCBOASUCBoltOnTypeAssArrDS.ListDS;
    for(var i=0;i<ucbota.length;i++){
        s.specificationCode = ucbota[i];

        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            ucbotaDS[i]=r;
            ucbotaDS[i].BoltOnTypeModEnum = ucbotaDS[i].BoltOnType;
            ucbotaDS[i].ThresholdEnum = ucbotaDS[i].Threshold;
        }
    }

}
return model;
