script.script:
if (model.specificationCode){

  var s = new epc.data.Specification();
  s.project =  cwa_epc.getDefaultProj();

  model.ServiceProviderEnum = model.ServiceProviderVoiceInputFor;
  model.NpiEnum = model.NpiVoiceInputFor;
  model.TonEnum = model.TonVoiceInputFor;
  model.DestinationsTypeEnum = model.DestinationTypeVoiceOutputOf;
  model.DestinationSubTypeEnum = model.DestinationSubTypeVoiceOutputOf;

  var Pat=model.PatternOfNumber;
  var Last=Pat.substring(Pat.length-1,Pat.length);
  if (Last=="*"){
      model.PatternOfNumberOptionEnum="Begins";
      model.PatternOfNumberTemp=Pat.substring(0, Pat.length-1);
  }
  else{
      model.PatternOfNumberOptionEnum="Exact";
      model.PatternOfNumberTemp=Pat;
  }
}
    return model;
