script.script:
if (!model.specificationCode){
    model = cm_spoc.initModelForCreation(model);
}else{
    model = cm_spoc.initModelForModification(model);

    var listRel = model.WURSWalletUsageRuleArrDS.ListDS;
    for ( var i = 0; i<listRel.length; i++){
        listRel[i].TariffPlanEnum = listRel[i].TariffPlan;
        listRel[i].DestinationTypeEnum = listRel[i].DestinationType;
        listRel[i].RoamingZoneEnum = listRel[i].RoamingZone;
        listRel[i].TribeEnum = listRel[i].Tribe;
        listRel[i].ListEnum = listRel[i].List;
        listRel[i].TribeCategoryEnum = listRel[i].TribeCategory;

    }

}

return model;
