script.script:
var s = new epc.data.Specification();
s.project =  cwa_epc.getDefaultProj();

if(node1){
    s.specificationCode = model.getValueByName(node1);
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
        model.putValueByName(node1+"DS",r);
    }
    else{
        var code = cm_spoc.initInnerDS("base_"+node1.substring(0,28));
        model.putValueByName(node1+"DS",code);
    }


    if(node2){
        s.specificationCode = model.getValueByName(node1+"DS").getValueByName(node2);
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
           model.getValueByName(node1+"DS").putValueByName(node2+"DS",r);
        }

        var code = model.getValueByName(node1+"DS").getValueByName(node2+"DS").List;
        var codeDS = model.getValueByName(node1+"DS").getValueByName(node2+"DS").ListDS;
        for(var i=0;i<code.length;i++){
            s.specificationCode = code[i];
            var r = epc.getSpecificationByKey(s);
            if(!api_common.isFault(r)){
                codeDS[i]=r;
            }

        }
    }

    if(node3){
        s.specificationCode = model.getValueByName(node1+"DS").getValueByName(node3);
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            model.getValueByName(node1+"DS").putValueByName(node3+"DS",r);
        }

        var code = model.getValueByName(node1+"DS").getValueByName(node3+"DS").List;
        var codeDS = model.getValueByName(node1+"DS").getValueByName(node3+"DS").ListDS;
        for(var i=0;i<code.length;i++){
            s.specificationCode = code[i];
            var r = epc.getSpecificationByKey(s);
            if(!api_common.isFault(r)){
                codeDS[i]=r;
            }
        }
    }
}
return model;
