script.script:
for(var i=0;i<array.length;i++)
{
    cm_spoc.loadSubOperations(array[i], iterator[i]);

    if(array[i].LifeCycleSubStatus!=null)
    {
        var s = new epc.data.Specification();
        s.project =  cwa_epc.getDefaultProj();

        s.specificationCode = array[i].LifeCycleSubStatus;
        var lifeCycleSubStatus = epc.getSpecificationByKey(s);
        if(lifeCycleSubStatus)
        {
            iterator[i].Status = lifeCycleSubStatus.Status;
            iterator[i].Substatus = lifeCycleSubStatus.Status;
            iterator[i].SubstatusDescription = lifeCycleSubStatus.DescriptionOfSubstatus;
        }
    }
}
