script.script:
//ui.model Rating Tree Node Output

ui.parent.model.RTNodeOutputArrDS.List=null;
ui.parent.model.RTNodeOutputArrDS.ListDS=null;

var values="";

for (var i = 0;i<selectedItems.length;i++) {
    var rtno =  cm_spoc.initInnerDS("base_RTNodeOutput");
    rtno = cm_spoc.initModelForCreation(rtno);
    rtno.NodeValue = selectedItems[i].id;

    ui.parent.model.RTNodeOutputArrDS.List[i]=rtno.specificationCode;
    ui.parent.model.RTNodeOutputArrDS.ListDS[i]=rtno;

    values = values ? values+", "+selectedItems[i].id : selectedItems[i].id;

}


ui.parent.parent.owner.values = values;
