script.script:
//The attribute must be saved only if the related attribute is checked and has the value shown in the column H.
//AwardDestinationPlanChange
//ChangeToPreviousPlan
//PromotionAward
//AwardType
//PC

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {

   if ((DS[k].AwardDestinationPlanChangeDS.ChangeToPreviousPlan) && (DS[k].AwardType != "PC"))
     {
      errList.addErrorMessage(data,["The attribute 'Change To Previous Plan' can be saved only if 'Award Type' is 'Destination Plan Change'."],"NGAA_003","E");
      return false;
     }
}
return false;
