//AwardType
// OB

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {
   if ((DS[k].AwardOperationOnBoltonDS.AOOBBoltOnTypeArrDS.ListDS.length > 0) && (DS[k].AwardType != "OB"))
     {
