script.script:
//The array must be saved only if the related attribute is checked and has the value shown in the column H.
//AOOCASAOpOnCAArr
//AOOCASAOpOnCAArr
//PromotionAward
//AwardType
// OC

//PromotionAward case
if(data.baseSpecificationCode == "base_PromotionAward"){
    if(data.AwardType != "OC" && data.AwardOperationOnCyclicActioDS.AOOCASAOpOnCAArrDS.specificationCode != null) {
        errList.addErrorMessage(data,["AOOCASAOpOnCAArrDS can be saved only if Promotion Award Type is OperationOnCyclicAction"],"NGAA_003","E");
    }
    return false;
}

//PromotionDueToExpense case
var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {

   if ((DS[k].AwardOperationOnCyclicActioDS.AOOCASAOpOnCAArrDS.ListDS.length > 0) && (DS[k].AwardType != "OC"))
     {
      errList.addErrorMessage(data,["The list 'Award Operation On Cyclic Action' can be saved only if 'Award Type' is 'Operation On Cyclic Action'."],"NGAA_003","E");
      return false;
     }
}
return false;
