//AOOLSimpAwOpOnListArr
//AOOLSimpAwOpOnListArr
//PromotionAward
//AwardType
//OL

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {
   if ((DS[k].AwardOperationOnListDS.AOOLSimpAwOpOnListArrDS.ListDS.length > 0) && (DS[k].AwardType != "OL"))
     {
      errList.addErrorMessage(data,["The list 'Award Operation On List' can be saved only if 'Award Type' is 'Operation On List'."],"NGAA_003","E");
