script.script:
//MC
if (data.AwardedTopUpPromotionType=="PT")
    return false;

if(data.AwardedByPermanentTopUpDS){
    var promotionSegment = false;
    for (var i=0;i<data.AwardedByPermanentTopUpDS.ABPTUATUPromPerSegmArrDS.ListDS.length;i++){
        if (data.AwardedByPermanentTopUpDS.ABPTUATUPromPerSegmArrDS.ListDS[i].AwardedPromotionSegment){
            promotionSegment = true;
            break;
        }
    }

    if (promotionSegment)
        errList.addErrorMessage(data,["Impossible to save AwaredPromotionSegment, AwardedTopUpPromotionType must be set to PT."],"NGAA_003","E");
}

return false;
