//ATCSimpAwSrvTCArr
//ATCSimpAwSrvTCArr
//PromotionAward
//AwardType
//TC

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {
   if ((DS[k].AwardSrvTariffChangeDS.ATCSimpAwSrvTCArrDS.ListDS.length > 0) && (DS[k].AwardType != "TC"))
     {
      errList.addErrorMessage(data,["The list 'Award Service Tariff Change' can be saved only if 'Award Type' is 'Tariff Change'."],"NGAA_003","E");
      return false;
     }
}
return false;
