script.script:
//The attribute must be mandatory only if the related attribute assumes one of the the values shown in column H.
//AwardTopUpAssignation
//Amount
//PromotionAward
//AwardType
// TU

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {

   if ((!DS[k].AwardTopUpAssignationDS.Amount) && (DS[k].AwardType == "TU"))
     {
      errList.addErrorMessage(data,["The attribute 'Amount' is mandatory if 'Award Type' is 'Top Up Assignation'."],"NGAA_003","E");
      return false;
     }
}
return false;
