script.script:
//The attribute must be saved only if the related attribute is checked and has the value shown in the column H.
//AwardTopUpAssignation
//ValidityPeriodInDays
//PromotionAward
//AwardType
// TU

var DS = data.PromotionDueToExpenseDS.PDTEPromotionAwardArrDS.ListDS;

for(var k =0; k< DS.length;k++) {

   if ((DS[k].AwardTopUpAssignationDS.ValidityPeriodInDays) && (DS[k].AwardType != "TU"))
     {
      errList.addErrorMessage(data,["The attribute 'Validity Period In Days' can be saved only if 'Award Type' is 'Top Up Assignation'."],"NGAA_003","E");
      return false;
     }
}
return false;
