script.script:
var setted = false;
for (var i=0; i<data.AwardedByLastTimeReceivedDS.ABLTRATUPhasePerSegmArrDS.ListDS.length; i++){
    if (data.AwardedByLastTimeReceivedDS.ABLTRATUPhasePerSegmArrDS.ListDS[i].RPTopUpAmountEachExecution){
        setted = true;
        break;
    }
}

if(setted && data.AwardedTopUpPromotionType!="LT" || data.AwardedTopUpPromotionType!="LO"){
    errList.addErrorMessage(data,["Impossible to save RPTopUpAmountEachExecution, AwardedTopUpPromotionType must be set to LT or LO."],"NGAA_003","E");
}
return false;
