script.script:
//MC
if (data.AwardedTopUpPromotionType=="LT" || data.AwardedTopUpPromotionType=="LO")
    return false;

//LT -> ATUPhasePerSegment
var destPairArrLT = false;
if (data.AwardedByLastTimeReceivedDS){
    for (var i=0; i<data.AwardedByLastTimeReceivedDS.ABLTRATUPhasePerSegmentArrDS.ListDS.length; i++){
        if (data.AwardedByLastTimeReceivedDS.ABLTRATUPhasePerSegmentArrDS.ListDS[i].ATUPhasePSTUODestPairArrDS
            && data.AwardedByLastTimeReceivedDS.ABLTRATUPhasePerSegmentArrDS.ListDS[i].ATUPhasePSTUODestPairArrDS.length>0){
            destPairArrLT = true;
            break;
        }
    }
}
//LO
var destPairArrLO = false;
if (data.AwardedByLoyaltyDS){
    for (var i=0; i<data.AwardedByLoyaltyDS.ABLTRATUPhasePerSegmentArrDS.ListDS.length; i++){
        if (data.AwardedByLoyaltyDS.ABLTRATUPhasePerSegmentArrDS.ListDS[i].ATUPhasePSTUODestPairArrDS
            && data.AwardedByLoyaltyDS.ABLTRATUPhasePerSegmentArrDS.ListDS[i].ATUPhasePSTUODestPairArrDS.length>0){
            destPairArrLO = true;
            break;
        }
    }
}

if (destPairArrLT || destPairArrLO)
    errList.addErrorMessage(data,["Impossible to save ATUPhasePSTUODestPairArr, AwardedTopUpPromotionType must be set to LT or LO."],"NGAA_003","E");

return false;
