script.script:
var ds = data.BankEntity.BankEntityFixedLimitArrayDS;

if ( ds.MinimumLimit > ds.MaximumLimit ) {
    // return true;
    errList.addErrorMessage(data,["Maximum Limit must be greater than Minimum Limit"],"NGAA_003","E");
}
else if (!ds.MinimumLimit) {
    // return true;
    errList.addErrorMessage(data,["Minimum Limit must be set"],"NGAA_003","E");
}
else {
    return false;
}
