script.script:
var BoltOnCodeLength = data.BoltOnCode.length;

if ( data.BoltOnTypeModalityCode == "PH" && BoltOnCodeLength > 4  ) {

   errList.addErrorMessage(data,["BoltOn","BoltOn Code must be maximum length 4 characters"],"NGAA_EPC_002","E");

}
else if ( data.BoltOnTypeModalityCode != "PH" && BoltOnCodeLength > 9  ){
     errList.addErrorMessage(data,["BoltOn","BoltOn Code must be maximum length 9 characters"],"NGAA_EPC_002","E");
}


var attrArr = {"name":"BoltOnCode","value":data.BoltOnCode};
var result = cm_spoc.isUniqueValue("base_BoltOn",[attrArr],data.specificationCode);

if(result != false)
{
    errList.addErrorMessage(data,["BoltOn Code already exists"],"NGAA_003","E");
}
return false;
