script.script:
var ds = data.BoltOnSubscriptionArrDS.ListDS;

for (var i = 0; i < ds.length; i++)
{
    var item = ds[i];
    var error = false;
    if( !item.CustomerProfile ){
        errList.addErrorMessage(data,["Customer Profile must be mandatory"],"NGAA_003","E");
        error = true;
    }

    if( !item.SubscriptionIncompatibleWith ){
        errList.addErrorMessage(data,["Incompatible Bolt On must be mandatory"],"NGAA_003","E");
        error = true;
    }

    if(!error){
        for(var j=i;j<ds.length;j++){
            if( (j!=i) && (ds[j].CustomerProfile==item.CustomerProfile) && (ds[j].SubscriptionIncompatibleWith==item.SubscriptionIncompatibleWith) ){
                    errList.addErrorMessage(data,["Duplicated row"],"NGAA_003","E");
            }
        }
    }
}
return false;
