script.script:
/*
 * data=cm_spoc.data.BoltOn
*/
 var bovods = data;

 if ( ( bovods.StartTime || bovods.EndTime ) && !(bovods.BoltOnValidityModality == "V_DATES") ) {

    //return true;
     errList.addErrorMessage(data,["Start date, End Date", "Validity Modality is not date range"],"NGAA_EPC_002","E");

 } else if ( ( bovods.Days || bovods.Hours || bovods.Minutes ) && !(bovods.BoltOnValidityModality == "V_TIME") ) {

    // return true;
     errList.addErrorMessage(data,["Days, Hours, Minutes", "Validity Modality is not time period"],"NGAA_EPC_002","E");

 }  else if ( ( bovods.CalendarDate ) && !(bovods.BoltOnValidityModality == "V_CALENDAR") ) {

    // return true;
    errList.addErrorMessage(data,["Calendar", "Validity Modality is not calendar date"],"NGAA_EPC_002","E");


 } else {

     return false;

 }
