script.script:
if(data.MaxThresholdOfAccumulatedTopUp && data.MinThresholdOfAccumulatedTopUp==null){
    errList.addErrorMessage(data,["Maximum Threshold of Accumulated Top Ups only can save if Minimum Threshold of Accumulated Top Ups is not empty"],"NGAA_003","E");
}
if(data.MaxThresholdOfAccumulatedTopUp < data.MinThresholdOfAccumulatedTopUp){
    errList.addErrorMessage(data,["Maximum Threshold of Accumulated Top Ups must be greater than Minimum Threshold of Accumulated Top Ups"],"NGAA_003","E");
}

return false;
