script.script:
//The attribute must be saved in the catalog only if the related attribute assumes the value shown in the column H
var dt = data;

if(dt.OutstandingExecutionsAreRecovere && dt.MaximumNumberOfDelays == null){
    errList.addErrorMessage(data,["MaximumNumberOfDelays must be not empty"],"NGAA_003","E");
}else{
    if(!dt.OutstandingExecutionsAreRecovere && dt.MaximumNumberOfDelays != null){
         errList.addErrorMessage(data,["MaximumNumberOfDelays must be empty"],"NGAA_003","E");
    }
}
return false;
