script.script:
if( cm_spoc.getExistingItem(data.specificationCode) )
   return false;

var attrArr = {"name":"ChargingModality","value":data.ChargingModality};
var result = cm_spoc.isUniqueValue("base_ChargingMode",[attrArr],data.specificationCode);

if(result != false)
{
    errList.addErrorMessage(data,["Charging Modality already exists"],"NGAA_003","E");
}
return false;
