script.script:
var entityArray = data.ListDS;

for(var i=0;i<entityArray.length;i++){
    for(var j=i+1;j<entityArray.length;j++){
            if(entityArray[i].ConstraintAssignedToAnOriginOp == entityArray[j].ConstraintAssignedToAnOriginOp
               && entityArray[i].ConstraintIncludesADestinationOp == entityArray[j].ConstraintIncludesADestinationOp
            ){
                  var first = i+1;
                  var second = j+1;
                  errList.addErrorMessage(data,["In the Incompatibility tab the row "+first+" is equal to the row "+second],"NGAA_003","E");
            }
      }
}
return false;
