script.script:
var entityArray = data.ListDS;

for(var i=0;i<entityArray.length;i++){
    for(var j=i+1;j<entityArray.length;j++){
        if(entityArray[i].Operation == entityArray[j].Operation
        && entityArray[i].PromotionalProfile == entityArray[j].PromotionalProfile
        && cm_spoc.SubOperationAttributesCompare(data,errList,entityArray[i].SubOperationDS,entityArray[j].SubOperationDS) == true
        ){
            var first = i+1;
            var second = j+1;
            errList.addErrorMessage(data,["In the tab Not allowed Operations by Promotional Profile the row "+first+" is equal to the row "+second],"NGAA_003","E");
        }
    }
}

return false;
