script.script:
if (!cm_spoc.checkModality(data.BoltOnTypeModality , data.ConsumptionRate, ["DT","MI", "ME"], true, null)) errList.addErrorMessage(data,["Consumption Rate","Value Not Allowed for this modality"],"NGAA_EPC_002","E");

return false;

/*
 * If the ConsumptionRate attribute of the BoltOnType is set, the BoltOnTypeModality MUST have one of the following values: [DT,MI]
 * data= BoltOnType
 * ent=ConsumptionRate
 */
var json =
      {"data" :
            [
                  {
                        "entityAttribute" : "ConsumptionRate" ,
                        "admissibleValues" : ["DT","MI", "ME"]
                  }

            ]
      };

var ret = false; //Default return value is TRUE => No Error

for (var i=0;i<json.data.length;i++) {

    var ent = json.data[i].entityAttribute;

    if (data[ent]) {
        ret = true; // If the leaf exist and is set to true we need to check that the modality value is one fo the admissible ones that's why we set ret to TRUE
        for (var k=0; k<json.data[i].admissibleValues.length; k++) {

            var val = json.data[i].admissibleValues[k];

            if(data.BoltOnTypeModality == val ) {

                ret = false; // The Modality value is admissible for the leaf. the return value is set to false : No Error
            }


        }
    }

    if (ret) {
        errList.addErrorMessage(data,["Consumption Rate","Value Not Allowed for this modality"],"NGAA_EPC_002","E");
        ret=false;
    }

}

return ret;
