script.script:
var attrArr = {"name":"CurrencyCode","value":data.CurrencyCode};

var isEmptyField = cm_spoc.isEmptyField([attrArr]);

if(isEmptyField)
{
    errList.addErrorMessage(data,["Some fields are empty."],"NGAA_003","E");
    return false;
}

var result = cm_spoc.isUniqueValue("base_Currency",[attrArr],data.specificationCode);

if(result != false)
{
    errList.addErrorMessage(data,["Currency with the entered Currency Code already exists."],"NGAA_003","E");
}

return false;
