if(ds.EventFirstUseAfterRenewal==null && ds.EventFirstUse)
{
    errList.addErrorMessage(data,["EventFirstUseAfterRenewal must be mandatory"],"NGAA_003","E");
}
if(ds.EventFirstUseAfterRenewal && !ds.EventFirstUse)
{
    errList.addErrorMessage(data,["EventFirstUseAfterRenewal is set but EventFirstUse is different from the allowed values"],"NGAA_003","E");
}
