script.script:
var value1=data.GeneralBoltOnValuesSpecDS;

if(hasBoltOnValue(value1.BoltOnValue) && value1.GeneralForBoltOn!=true && cm_spoc.checkInclusion(data.BoltOnTypeModality,["BD","CR","DT","ME","MI","MC","MG","GB"]))
   errList.addErrorMessage(data,["General For Bolt On Must Be Selected if Bolt On Value is set"],"NGAA_003","E");
else if (hasBoltOnValue(value1.BoltOnValue) && value1.GeneralForBoltOn==true && !cm_spoc.checkInclusion(data.BoltOnTypeModality,["BD","CR","DT","ME","MI","MC","MG","GB"]))
   errList.addErrorMessage(data,["Bolt On Type Modality must be BD, CR, DT, ME, MI, MC, MG or GB if Bolt On Value is set"],"NGAA_003","E");
else if (hasBoltOnValue(value1.BoltOnValue) && value1.GeneralForBoltOn!=true && !cm_spoc.checkInclusion(data.BoltOnTypeModality,["BD","CR","DT","ME","MI","MC","MG","GB"]))
    errList.addErrorMessage(data,["Bolt On Type Modality must be BD, CR, DT, ME, MI, MC, MG or GB and General For Bolt On Must Be Selected if Bolt On Value is set"],"NGAA_003","E");
return false;


function hasBoltOnValue(boltOnValue){
    return boltOnValue!=null && boltOnValue!=0;
}
