                     ]
            };



for ( var i=0;i<json.data.length; i++) {

if (data[json.BoltOnTypeAttribute][json.data[i].entity]  && !data[json.BoltOnTypeAttribute][json.data[i].relEntity] ) {
     errList.addErrorMessage(data,["General For Bolt On Must Be Selected"],"NGAA_003","E");
} else if (data[json.BoltOnTypeAttribute][json.data[i].entity]  && data[json.BoltOnTypeAttribute][json.data[i].relEntity] ) {

          var ret=true;
          for (var k=0; k<json.data[i].modality.length; k++) {

