script.script:
var dt=data;

var modality = dt.BoltOnTypeModality;

if ( dt.GeneralBoltOnValuesSpecDS.PercentageReductionInLD!=null && dt.GeneralBoltOnValuesSpecDS.ApplyReductionInLongDistance==false  && cm_spoc.checkInclusion(modality, ["CR"]))
         errList.addErrorMessage(data,["ApplyReductionInLongDistance must be set"],"NGAA_003","E");
else if ( dt.GeneralBoltOnValuesSpecDS.PercentageReductionInLD!=null && dt.GeneralBoltOnValuesSpecDS.ApplyReductionInLongDistance==true  && !cm_spoc.checkInclusion(modality, ["CR"]))
         errList.addErrorMessage(data,["PercentageReductionInLD only save if BoltOnTypeModality is CR or PH"],"NGAA_003","E");
else if ( dt.GeneralBoltOnValuesSpecDS.PercentageReductionInLD!=null && dt.GeneralBoltOnValuesSpecDS.ApplyReductionInLongDistance==false  && !cm_spoc.checkInclusion(modality, ["CR"]))
         errList.addErrorMessage(data,["ApplyReductionInLongDistance must be set and BoltOnTypeModality must be CR or PH"],"NGAA_003","E");

return false;
