script.script:
/*
 * data = cm_spoc.data.BoltOnType
 *
 * If data.GeneralBoltOnValuesSpecificationDS.BoltOnValue. :
    ApplyReductionInLongDistance
    UnitsOfACompleteCycle
    LastFreeUnitsInTheCycle
    PercentageReductionInLongDistance
    are set we must have that:
 * GeneralForBoltOn = TRUE

 *
 * data=cm_spoc.data.BoltOnType
 */

var json = {
            "BoltOnTypeAttribute" : "GeneralStartEndDateSpecDS",
             "data" : [
                        { "entity" : "StartDateTime" , "relEntity" : "GeneralForBoltOn" } ,
                        { "entity" : "EndDateTime" , "relEntity" : "GeneralForBoltOn" }
                     ]
            };



for ( var i=0;i<json.data.length; i++) {

    if (data[json.BoltOnTypeAttribute][json.data[i].entity]  && !data[json.BoltOnTypeAttribute][json.data[i].relEntity] ) {

        errList.addErrorMessage(data,["General For BoltOn" ] , "SVR_1050010004","E");
        return false;
    }

}

return false;
