script.script:
var ds = data.IVRBoltOnDescriptionArrDS.ListDS;
var ivrCode= data.IVRBoltOnCode;
if( ds.length==0 && !(ivrCode=="" || ivrCode==null))
{
 errList.addErrorMessage(data,["IVR Bolt On Language and Description mandatory if set IVR Bolt On Code "],"NGAA_003","E");
}

for (var i = 0; i < ds.length; i++)
{
    var item = ds[i];
    var error = false;
    if( !item.IVRBoltOnDescription ){
        errList.addErrorMessage(data,["Description mandatory if set IVR Bolt On Code"],"NGAA_003","E");
        error = true;
    }

    if( !item.Language ){
        errList.addErrorMessage(data,["Language mandatory if set IVR Bolt On Code"],"NGAA_003","E");
        error = true;
    }

    if(!error){
        for(var j=i;j<ds.length;j++){
            if( (j!=i) && (ds[j].Language==item.Language) ){
                    errList.addErrorMessage(data,["Duplicated language: " + ds[j].Language],"NGAA_003","E");
            }
        }
    }
}


return false;
