script.script:
/**Conditional
 *
 * LCGPROutstandingStatRetrievedTUWalletCondRule
 *
 * The attribute must be saved only if the related attribute in column H has at least one element checked.
 *
 * LifeCycleGracePeriodRule
 * OutstandingStatRetrievedTUWallet
 *
 * RecoveryCondition
 * RetrievablePC

 *
 * TRUE
*/


var listArrayDS=data.RecoveryConditionArrDS.ListDS;
var checkedPC =false;
var dim = listArrayDS.length;
 for (var i=0; i<dim; i++){
     if (listArrayDS[i].RetrievablePC)
         checkedPC = true;
 }
 if(checkedPC==false && data.OutstandingStatRetrievedTUWallet!=null)
         errList.addErrorMessage(data,["Wallet not saved","RetrievablePC Invalid"],"NGAA_EPC_002","E");


return false;
